mapper.select:
SELECT member_id as memberId, place_id as placeId, title, content, picture, rate, like_cnt as likeCnt, created_date as createDate, last_modified_date as lastModifiedDate
FROM review
WHERE place_id = #{placeId}

SELECT member_id as memberId, place_id as placeId, title, content, picture, rate, like_cnt as likeCnt, created_date as createDate, last_modified_date as lastModifiedDate
FROM review
WHERE id = #{reviewId}

mapper.update:
UPDATE review
SET title = #{title}, content=#{content}, picture=#{picture}, rate=#{rate}, last_modified_date = now()
WHERE id = #{id}

UPDATE review
SET like_cnt = like_cnt + 1
WHERE id = #{reviewId}

UPDATE review
SET like_cnt = like_cnt - 1
WHERE id = #{reviewId}
